local decoded_W = {}

-- Step 1: convert escaped chars into real bytes
for i, s in ipairs(W) do
    local real_str = s:gsub("\\(%d%d?%d?)", function(d)
        return string.char(tonumber(d))
    end)
    decoded_W[i] = real_str
end

-- Step 2: Base64 decode each string
local function base64_decode(data)
    local b='ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/'
    data = string.gsub(data, '[^'..b..'=]', '')
    return (data:gsub('.', function(x)
        if x == '=' then return '' end
        local r,f='',(b:find(x)-1)
        for i=6,1,-1 do r=r..(f%2^i - f%2^(i-1) > 0 and '1' or '0') end
        return r
    end):gsub('%d%d%d%d%d%d%d%d', function(x)
        return string.char(tonumber(x,2))
    end))
end

local final_W = {}
for i, s in ipairs(decoded_W) do
    final_W[i] = base64_decode(s)
end

-- Print results (hex or raw bytes)
for i, s in ipairs(final_W) do
    print(i, s)
end
